import React, {useEffect, useState} from 'react';
import {TodosService} from "../core/services";

const TodoList = () => {
  const [todos, setTodos] = useState<any>([]);
  const [newTodo, setNewTodo] = useState('');

  useEffect(() => {

  }, [])

  const getTodos = () => {
    TodosService.getAll().then(res => setTodos(res))
      .catch(error => console.log('err', error))
  }

  const addTodo = () => {
    console.log('ушел ', newTodo)
    TodosService.create({title: newTodo})
      .then(res => {
        setTodos([...todos, res]);
        setNewTodo('');
      })
      .catch(error => console.log('something went wrong', error))
    // if (newTodo.trim()) {
    //   console.log(newTodo);
    //   setTodos([...todos, {id: 1, title: newTodo}])
    //   setNewTodo('');
    //   // axios
    //   //   .post('http://localhost:3000/todos', { title: newTodo })
    //   //   .then((response) => {
    //   //     setTodos([...todos, response.data]);
    //   //     setNewTodo('');
    //   //   });
    // }
  };

  return (
    <div>
      <div>
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          placeholder="Enter a task"
        />
        <button disabled={!newTodo.trim()} onClick={addTodo}>Add</button>
      </div>
      {todos.length === 0 ? (
        <p>No tasks to display</p>
      ) : (
        <ul>
          {todos.map((todo: any) => (
            <li key={0}>
              <p>{todo.title}</p>
              <button>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TodoList;
