import React, {useEffect, useState} from 'react';
import {TodosService} from "../core/services";
import {TTodo} from "../core/types";

import './index.css';

const TodoList = () => {
  const [todos, setTodos] = useState<TTodo[] | []>([]);
  const [newTodo, setNewTodo] = useState<string>('');

  useEffect(() => {
    getTodos();
  }, [])

  const getTodos = () => {
    TodosService.getAll().then(res => setTodos(res))
      .catch(error => console.log('err', error))
  }

  const addTodo = () => {
    TodosService.create({title: newTodo})
      .then(() => {
        getTodos();
        setNewTodo('');
      })
      .catch(error => console.log('something went wrong', error))
  };

  const removeTodo = (id: number) => {
    TodosService.remove(id)
      .then(() => {
        getTodos();
        setNewTodo('');
      })
      .catch(error => console.log('something went wrong', error))
  };

  const toggleCompletion = async (id: number, currentStatus: boolean) => {
    try {
      // const response = await axios.patch(`http://localhost:3000/todos/${id}`, {
      //   isCompleted: !currentStatus,
      // });

      // Если сервер успешно обновил задачу, обновляем локальное состояние
      setTodos((prevTodos) =>
        prevTodos.map((todo) =>
          todo.id === id ? { ...todo, isCompleted: !currentStatus } : todo
        )
      );
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };


  return (
    <div>
      <div>
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          placeholder="Enter a task"
        />
        <button disabled={!newTodo.trim()} onClick={addTodo}>Add</button>
      </div>
      {todos.length === 0 ? (
        <p>No tasks to display</p>
      ) : (
        <ul>
          {todos.map((todo: TTodo) => (
            <li className={'todo'} key={todo.id}>
              <p>{todo.title}</p>
              <div>
                <button
                  style={{marginRight:15}}
                  onClick={() => removeTodo(todo.id)}
                >
                  Delete
                </button>
                <button onClick={() => toggleCompletion(todo.id, todo.isCompleted)}>Completed</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TodoList;
