import axios, { AxiosError, AxiosRequestConfig, Method } from 'axios';

axios.defaults.timeout = Number(process.env.NEXT_PUBLIC_API_TIMEOUT_MS) || 3000;

interface IRequest {
  method?: Method;
  url: string;
  data?: any;
  contentType?: string;
  baseURL?: string;
  lang?: string | null;
}

type TError = {
  code: string;
  message: any;
  status: number;
};

export const request = ({
  method = 'POST',
  url = '',
  data = null,
  contentType = 'application/json',
  lang = null,
  baseURL = 'http://localhost:3001/api/',
}: IRequest): Promise<any> => {
  const options: AxiosRequestConfig = {
    method,
    baseURL,
    url,
    headers: { 'Content-Type': contentType },
  };

  if (data && method === 'GET') {
    options.params = data;
  } else if (data) {
    options.data = data;
  }

  const errorHandler = (axiosError: AxiosError) => {
    let error: TError = { code: '', message: '', status: 0 };
    if (axiosError.response) {
      error.status = axiosError.response.status;
      error.code = axiosError.response?.data?.code || '';
      error.message = axiosError.response?.data?.message || '';
    } else {
      error.code = 'NETWORK_ERROR';
      error.message = 'Network error';
      error.status = 600;
    }
    return error;
  };

  return new Promise((resolve, reject) => {
    axios(options)
      .then(response => resolve(response.data))
      .catch(error => reject(errorHandler(error)));
  });
};
